@use "variables" as *;
@use "framework" as *;
@use "apropos" as *;
@use "articlesgeneral" as *;
@import "@fortawesome/fontawesome-free/css/all.css";
// @import "node_modules/bootstrap/scss/bootstrap";

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  background-color: $couleur-fond;
}

/* TITRES ET PARAGRAPHES */

h4 {
  font-size: $titre-pc-moyen;
  font-family: $secondary-font;
}

/* NAVBAR */

.nav-bar {
  background-color: $couleur-fond;
  z-index: 10;
  .nav-logo {
    width: 10%;
    margin-left: 50px;
  }
  .nav-logo-sm {
    display: none;
  }
  ul {
    margin-left: auto;
    li {
      a {
        font-family: $main-font;
        font-size: $texte-footer;
        color: $noir;
      }
    }
  }
}

.burger-menu {
  display: none;
}

.dropdown-sm {
  display: none;
}

.dropdown-article-pc {
  display: none;
  z-index: 5;
}

.dropdown-article {
  position: relative;
}

.nav-links {
  li {
    a.dropdown-article:hover {
      .dropdown-article-pc {
        display: block;
        position: absolute;
        overflow: hidden;
      }
    }
  }
}

.dropdown-article:hover {
  .dropdown-article-pc {
    display: block;
    position: absolute;
    overflow: hidden;
  }
}

@media only screen and (max-width: 720px) {
  body {
    overflow-x: hidden;
  }
  .burger-menu {
    display: block;
  }
  .nav-bar {
    .nav-logo {
      display: none;
    }
    .nav-logo-sm {
      display: block;
      width: 15%;
      margin-left: 10px;
      z-index: 3;
    }
  }
  .burger-menu {
    cursor: pointer;
    margin-right: 15px;
    margin-left: auto;
    z-index: 4;
    .line1,
    .line2,
    .line3 {
      width: 30px;
      height: 3px;
      background-color: $vert-fonce;
      margin: 5px;
      border-radius: 12px;
    }
  }

  .nav-links {
    position: absolute;
    right: 0px;
    top: 7vh;
    height: 90%;
    width: 100%;
    background-color: $couleur-fond;
    display: flex;
    flex-direction: column;
    align-items: center;
    z-index: 2;
    display: none;
  }

  .nav-text {
    font-weight: $demi-bold;
  }

  .dropdown-article {
    display: flex;
    align-items: center;
    justify-content: center;
  }
  .dropdown-sm {
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 0;
  }

  .nav-links.open {
    display: flex;
  }
}

.toggle {
  div.line1 {
    transform: rotate(-45deg) translateY(11px);
  }
  div.line2 {
    opacity: 0;
  }
  div.line3 {
    transform: rotate(+45deg) translateY(-11px);
  }
  div {
    transition: all 0.1s ease;
  }
}

/********** PAGES **********/
/***** HOMEPAGE ******/

.homepage-section {
}
.homepage-right {
  width: 50%;
  height: 100%;
}
.homepage-img {
  margin: 30px 80px;
