    margin: 30px auto;
    img {
      width: 100%;
    }
  }

  .homepage-right {
    width: 100%;
  }

  .homepage-intro {
    width: 90%;
    height: auto;
    padding: 60px;
    margin: 40px auto;
    transform: translateX(0%) translateY(-30%);

